var OAE = require('oae-util/lib/oae');

var ArxivAPI = require('./api');

OAE.tenantRouter.on('get', '/api/arxiv', function(req, res) {
    ArxivAPI.getArxivProfile(req.ctx, 'http://arxiv.org/abs/1402.0928', function(err, arxiv) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200, arxiv);
    });
});
