var _ = require('underscore');
var OAE = require('oae-util/lib/oae');

var TodoAPI = require('oae-todo');


//////////////////
// Tenant admin //
//////////////////

/*!
 * Get a user's todo list
 */
OAE.tenantRouter.on('get', '/api/todo/todos', function(req, res) {
    TodoAPI.getTodos(req.ctx, function(err, todos) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200, todos);
    });
});

/*!
 * Add an item to the todo list
 */
OAE.tenantRouter.on('post', '/api/todo/todos', function(req, res) {
    TodoAPI.addTodo(req.ctx, req.body, function(err, todos) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200, todos);
    });
});

/*!
 * Mark a todo as 'done'
 */
OAE.tenantRouter.on('post', '/api/todo/:id/done', function(req, res) {
    TodoAPI.markTodoAsDone(req.ctx, req.params.id, req.body.done, function(err, todo) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200, todo);
    });
});
